dataSample.sql:
CREATE TABLE IF NOT EXISTS `PREFIX_leoblog_blog` ( `id_leoblog_blog` int(11) NOT NULL AUTO_INCREMENT, `id_leoblogcat` int(11) NOT NULL, `position` int(11) NOT NULL, `date_add` date NOT NULL, `active` tinyint(1) NOT NULL, `user_id` int(11) NOT NULL, `hits` int(11) NOT NULL, `image` varchar(255) DEFAULT NULL, `thumb` varchar(255) DEFAULT NULL, `date_upd` datetime NOT NULL, `video_code` text DEFAULT NULL, `params` text DEFAULT NULL, `featured` tinyint(1) NOT NULL, `favorite` tinyint(1) NOT NULL, `indexation` int(11) NOT NULL, `id_employee` int(11) NOT NULL, `product_ids` varchar(255) DEFAULT NULL, `author_name` varchar(255) DEFAULT NULL, PRIMARY KEY (`id_leoblog_blog`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leoblog_blog_lang` ( `id_leoblog_blog` int(11) NOT NULL, `id_lang` int(11) NOT NULL, `meta_description` varchar(255) NOT NULL, `meta_keywords` varchar(250) NOT NULL, `meta_title` varchar(250) NOT NULL, `subtitle` varchar(250) DEFAULT NULL, `link_rewrite` varchar(255) NOT NULL, `content` text NOT NULL, `description` text NOT NULL, `tags` varchar(255) NOT NULL, PRIMARY KEY (`id_leoblog_blog`,`id_lang`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leoblog_blog_shop` ( `id_leoblog_blog` int(11) NOT NULL DEFAULT 0, `id_shop` int(11) NOT NULL DEFAULT 0, PRIMARY KEY (`id_leoblog_blog`,`id_shop`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leoblog_comment` ( `id_comment` int(11) unsigned NOT NULL AUTO_INCREMENT, `id_shop` int(11) NOT NULL DEFAULT 0, `id_leoblog_blog` int(11) unsigned NOT NULL, `comment` text NOT NULL, `active` tinyint(1) NOT NULL DEFAULT 0, `date_add` datetime DEFAULT NULL, `user` varchar(255) NOT NULL, `email` varchar(255) NOT NULL, PRIMARY KEY (`id_comment`,`id_shop`), KEY `FK_blog_comment` (`id_leoblog_blog`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leoblogcat` ( `id_leoblogcat` int(11) NOT NULL AUTO_INCREMENT, `image` varchar(255) NOT NULL, `id_parent` int(11) NOT NULL, `item` varchar(255) DEFAULT NULL, `level_depth` smallint(6) NOT NULL, `active` tinyint(1) NOT NULL, `show_title` tinyint(1) NOT NULL, `position` int(11) NOT NULL, `submenu_content` text NOT NULL, `privacy` smallint(6) DEFAULT NULL, `position_type` varchar(25) DEFAULT NULL, `menu_class` varchar(25) DEFAULT NULL, `content` text DEFAULT NULL, `icon_class` varchar(255) DEFAULT NULL, `level` int(11) NOT NULL, `left` int(11) NOT NULL, `right` int(11) NOT NULL, `date_add` datetime DEFAULT NULL, `date_upd` datetime DEFAULT NULL, `template` varchar(200) NOT NULL, `randkey` varchar(255) DEFAULT NULL, PRIMARY KEY (`id_leoblogcat`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leoblogcat_lang` ( `id_leoblogcat` int(11) NOT NULL, `id_lang` int(11) NOT NULL, `title` varchar(255) DEFAULT NULL, `content_text` text DEFAULT NULL, `description` varchar(200) NOT NULL, `meta_keywords` varchar(255) NOT NULL, `meta_description` varchar(255) NOT NULL, `link_rewrite` varchar(250) NOT NULL, PRIMARY KEY (`id_leoblogcat`,`id_lang`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leoblogcat_shop` ( `id_leoblogcat` int(11) NOT NULL DEFAULT 0, `id_shop` int(11) NOT NULL DEFAULT 0, PRIMARY KEY (`id_leoblogcat`,`id_shop`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
